var roadMapApp = angular.module('app', [
    'coreMod',
    'navigationMod',
    'footerMod',
    'markdownMod',
    'app/documentation/views'
  ]);
roadMapApp.config([
  '$stateProvider',
  'markdownModProvider',
  function ($stateProvider, markdownModProvider) {
    console.log('documentation.config');
    var page = {
        url: '',
        views: {
          'sideBar': { templateUrl: 'app/documentation/views/sidebar.html' },
          '': {
            templateUrl: 'app/documentation/views/content.html',
            controller: function ($scope) {
              markdownModProvider.setSource('md/documentation/intro.md');
            }
          }
        }
      };
    $stateProvider.state('documentation', page).state('documentation_section', {
      url: '/{sectionId}',
      views: {
        'sideBar': { templateUrl: 'app/documentation/views/sidebar.html' },
        '': {
          templateUrl: 'app/documentation/views/content.html',
          controller: function ($scope, $stateParams) {
            markdownModProvider.setSource('md/documentation/' + $stateParams.sectionId + '.md');
          }
        }
      }
    });
  }
]);