var roadMapApp = angular.module('app', [
    'coreMod',
    'navigationMod',
    'footerMod',
    'markdownMod',
    'app/road_map/views'
  ]);
roadMapApp.config([
  '$stateProvider',
  'markdownModProvider',
  function ($stateProvider, markdownModProvider) {
    console.log('roadMapApp.config');
    var page = {
        url: '',
        views: {
          'sideBar': { templateUrl: 'app/road_map/views/sidebar.html' },
          '': { templateUrl: 'app/road_map/views/content.html' }
        }
      };
    $stateProvider.state('road_map', page);
    markdownModProvider.setSource('md/road_map.md');
  }
]);